#!/opt/stack/bin/python3
import shlex import subprocess import os import stack.api
FNULL = open(os.devnull, 'w')
stack.api.Call('remove.host.partition', [ 'a:backend' ]) stack.api.Call('set.host.attr', [ 'a:backend', 'attr=nukedisks', 'value=true' ]) stack.api.Call('set.host.attr', [ 'a:backend', 'attr=nukecontroller', 'value=true' ]) stack.api.Call('set.host.boot', [ 'a:backend', 'action=install' ])
for o in stack.api.Call('list.host', [ 'a:backend' ]): cmd = 'ipmitool -I lanplus -U root -P admin -H %s.ipmi chassis power cycle' % o['host'] p = subprocess.run(shlex.split(cmd), stdout = FNULL, stderr = FNULL)
