stack:stack.stack:script:
#!/opt/stack/bin/python3

print('#!/bin/sh')
print('log "S07-stacki-readonly-terminal..."')
print('log "========================"')

f = open('/proc/cmdline')
line = f.readline().split()
f.close()

for l in line:
	if l.startswith('console='):
		a = l.split('=')
		for tty in a[1].split(','):
			if tty.startswith('ttyS'):
				print('stty -F /dev/%s -cread' % (tty))
				break


/tmp/get_serial_tty > /usr/lib/YaST2/startup/Second-Stage/S07-stacki-readonly-terminal
chmod 755 /usr/lib/YaST2/startup/Second-Stage/S07-stacki-readonly-terminal
