stack:script.stack:file:
/var/log/audit/audit.log {
	size=100M
	postrotate
		/bin/kill -HUP `cat /var/run/syslogd.pid 2> /dev/null` \
			2> /dev/null || true
	endscript
}
/var/log/local[0-9] {
	size=10M
	postrotate
		/bin/kill -HUP `cat /var/run/syslogd.pid 2> /dev/null` \
			2> /dev/null || true
	endscript
}
/var/log/checklist.log {
	size=100M
	postrotate
		/usr/bin/systemctl restart checklist
	endscript
}
