import os import sys import subprocess
efi_dev = None mtab = open('/etc/mtab','r') for mountline in mtab.readlines(): m = mountline.split() if m[1].strip() == '/mnt/boot/efi': efi_dev = m[0].strip() break
if efi_dev: f = open('/mnt/etc/mtools.conf','a') f.write('\ndrive x: file="%s"\n' % efi_dev) f.close()
